INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (112, 'Berliner Schachgesellschaft 1827 Eckbauer                ', '1827 gegründet', '', 'Die Berliner Schachgesellschaft 1827 Eckbauer ging nach dem 2.Weltkrieg am 3.7.1949 aus den Vereinen [VERWEIS=178]Berliner Schachgesellschaft[/VERWEIS] und der [VERWEIS=14]Schachvereinigung Eckbauer[/VERWEIS] hervor. Anfangs nannte sich der Verein nur Schachgesellschaft Eckbauer, 1953 erfolgte die Umbenennung.
