INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (126, 'Spiel- und Sportverein Rotation Berlin                   ', 'Nov.1951   gegründet', '                                    ', 'Gegründet als BSG Rotation Berlin, nach 1990 heutiger Name.<br>2004 zerfiel der Verein in zwei Teile, dessen Ursache die unsichere Spielstätte in der Kanzowstr. war. Die oberen 4-5 Mannschaften gründeten mit dem SC Rotation Pankow einen eigenen Verein und übernahmen die vorhandenen Spielberechtigungen. Die Trennung erfolgte einvernehmlich.', 0, 0, 'Berlin', 1);
