INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (134, 'Sportgemeinschaft NARVA Berlin', '01.07.1950 gegründet', '', '[BILD=0]Der Gesamtverein wurde als Betriebssportvereinigung (BSV) Berliner Glühlampenwerk am 1.April 1949 gegründet, die Sparte Schach ein Jahr später. 1951 umbenannt in BSG Motor Friedrichshain – Ost, 1955 in BSG Motor Berliner Glühlampenwerk, 1969 in BSG NARVA Berlin. Seit 1991 heißt der Verein SG NARVA Berlin e.V. 
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (135, 'BSV 63 Chemie Weißensee                                  ', '04.09.1964 gegründet', '                                    ', 'Gegründet als BSG Gummiwerke, später umbenannt in BSG Chemie Weißensee, seit 1990 heutiger Name. 1986 war für Chemie Weißensee eine schwierige Zeit. Zu dieser Zeit hatte man das Spiellokal in der Gustav-Adolf-Straße verloren und war bei Rotation Kunst am Arnimplatz Untermieter. Die Nachrufe waren wohl schon geschrieben. Aber der Verein konnte sich nicht zuletzt durch die Anstrengungen von Andreas Zahn behaupten und ging sogar gestärkt aus dieser Krise hervor.', 0, 0, 'Berlin', 1);
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (136, 'Schachgemeinschaft Eckturm                               ', '1964 gegründet', '                                    ', 'Werner Wiesner sagte auf einer Verbandssitzung am 9.10.2006, das der Verein aus der Schachsektion der Hochschule für Ökonomie Karlshorst hervorging.', 0, 0, 'Berlin', 1);
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (137, 'Sportverein Bau-Union Berlin e.V.', '1970 gegründet', '', 'Der Verein nannte sich anfangs <i>VSG Lichtenberger Schachfreunde</i>, danach <i>BSG KWV Berlin</i>, <i>BSG WBK Berlin</i> und trägt seit etwa 1990 den heutigen Namen.
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (138, 'Sportclub Eintracht Berlin, Abteilung Schach', '1970 gegründet', '', '1970 als Medizin Lichtenberg gegründet. 1982 Umbenennung in Medizin Marzahn, 1987 in Medizin Berlin. Auflösung 1991 - die Abmeldung beim Berliner Schachverband erfolgte am 15.12.91. Letzter Postempfänger war Frank Hoppe.
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (139, 'Schachfreunde Friedrichshagen', 'mind. 1951 gegründet', '', 'Gegründet als SG Friedrichshagen. Nannte sich von mind. 1963 bis 1990 (?) BSG Chemie Köpenick. Die Betriebssportgemeinschaft benannte sich danach um in <i>Sportverein Grün-Weiß Köpenick</i>. Im Sommer 2006 machten sich die Schachspieler unter ihrem jetzigen Namen selbständig.
