INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (141, 'Sportverein Turbine Berlin                               ', 'mind. 1971 gegründet', '                                    ', 'Nannte sich bis in die 70er Jahre Turbine Bewag, später Turbine EKB Treptow. Nach der Wende (1990) traten die Spieler der HSG Humboldt-Uni dem Verein bei.', 0, 0, 'Berlin', 1);
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (142, 'TSG Rot-Weiß Fredersdorf/Vogelsdorf', '1948 gegründet', '', '<ul><li>Die Schachabteilung existierte nie als eigenständiger Verein und wurde 1948 gegründet.</li><li>Der Gesamtverein trug schon die verschiedensten Namen. BSG Tiefbau Fredersdorf ist einer davon.</li><li>Nach dem Beitritt der DDR zur BRD im Jahr 1990 und der nachfolgenden Gebietsreform in den neuen Bundesländern entstand mit der Bildung des Doppeldorfes aus Fredersdorf und Vogelsdorf durch eine Fusion der beiden Vereine TSG Fredersdorf und Rot-Weiß Vogelsdorf der jetzige Gesamtverein.</li></ul><i>Martin Sebastian</i>', 1948, 0, 'Berlin', 1);
