INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (145, 'Sportverein Berlin-Friedrichstadt                        ', '01.01.1981 gegründet', '                                    ', 'Der Schachverein wurde am 01.01.1981 auf Initiative von Heinz Jäger in der damaligen Betriebssportgemeinschaft Aussenhandel ins Leben gerufen. Daraus bildete sich nach der Vereinigung Deutschlands am 27.03.1991 der heutige Verein SV Berlin-Friedrichstadt mit der Abteilung Schach.', 0, 0, 'Berlin', 1);
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (146, 'Treptower Sportverein 1949                               ', '1982       gegründet', '                                    ', '1982 von Hans Wolf als BSG EAW Treptow gegründet (EAW = Elektro-Apparate-Werke). Nach 1990 erfolgte die Umbenennung in den heutigen Namen.', 0, 0, 'Berlin', 1);
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (147, 'Schachverein Königsjäger Süd-West                        ', 'Mai 1982 gegründet', '                                    ', 'Entstand am 1.Januar 1992 aus einer Spielgemeinschaft (bestand seit 1.Juli 1990) der Vereine [VERWEIS=282]Schachklub Süd-West[/VERWEIS] und [VERWEIS=356]Zehlendorfer Königsjäger[/VERWEIS]. Beide Vereine spalteten sich zuvor vom [VERWEIS=123]SK Zehlendorf[/VERWEIS] ab: Süd-West im Mai 1982 und die Königsjäger 1988.', 1982, 0, 'Berlin', 1);
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (148, '1.VfL Fortuna Marzahn                                    ', '13.09.1984 gegründet, 2007 aufgelöst', '                                    ', 'Am 13. September 1984 wurde bei der BSG BWF Marzahn eine Sektion Schach gegründet. Tatkräftig daran beteiligt waren der spätere Vorsitzende der Emanuel Lasker Gesellschaft, Paul Werner Wagner, und Roland Hebert.
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (149, 'Berliner Sport-Club Rehberge 1945, Schachabteilung', '1930 gegründet', '/archiv/chronik/vereine/dok/bscrehberge/index.html', '1930 wurden die <i>Schachfreunde Reinickendorf</i> gegründet. 1932 trat Jacob Hillbrunner dem Verein bei, zwei Jahre später Martin Großmann. Die Spielstätte war ein Restaurant an der Scharnweber-, Ecke Blankestraße.
