INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (152, 'Schachclub Lavandevil Charlottenburg                     ', '28.07.1995 gegründet', '                                    ', '1991 schlossen sich einige nichtorganisierte Schachfreunde aus dem Restaurant "Lavandevil" als eigenständige Mannschaft dem SC Schwarzer Springer an. 1995 trennte man sich vom Schwarzen Springer und gründete einen eigenen Verein. Vorsitzender ist seit der Gründung Reinhard Rzepka.', 0, 0, 'Berlin', 1);
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (153, 'Schachclub Zugzwang 95                                   ', 'mind. 1995 gegründet', '                                    ', 'Nach zwei Jahren Spielgemeinschaft mit den Sfr.Friedrichshain, sind die Friedrichshainer im Jahr 2004 dem SC Zugzwang beigetreten. Einige Spieler gingen auch zum SC Rochade.', 0, 0, 'Berlin', 1);
