INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (157, 'Schachclub Post Wedding                                  ', '1998       gegründet', '                                    ', 'Spaltete sich 1998 vom SV Post ab. Den Spielern der ehemaligen BSG Post Berlin (die vom SV Post 1990 übernommen wurden) war der Weg in den tiefen Westen zu weit. Bis auf einen Spieler (den Vorsitzenden) wechselten alle Mitglieder !', 0, 0, 'Berlin', 1);
