INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (160, 'TSV Marienfelde 1890', '1994 gegründet', '', 'Die Schachabteilung ist eine der sieben sportlichen Abteilungen des TSV Marienfelde 1890 e.V. Sie wurde im Jahre 1994 von neun schachbegeisterten Sportlern gegründet und zählte im Jahr 2003 neunzehn Mitglieder. 
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (161, 'Schachclub Rotation Pankow                               ', '24.05.2004 gegründet', '                                    ', 'Spaltete sich einvernehmlich vom SSV Rotation ab. Nur noch die Jugendabteilung und einige wenige Erwachsene verblieben bei Rotation.', 0, 0, 'Berlin', 1);
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (162, 'Schachgesellschaft Lasker Steglitz-Wilmersdorf           ', 'seit 9.April 1999', '                                    ', 'Die SG Lasker entstand 1999 durch Zusammenschluss der Vereine [VERWEIS=254]SV Wilmersdorf[/VERWEIS] und [VERWEIS=344]SVg Lasker-Steglitz[/VERWEIS]. Formal geschah dies durch Auflösung der SVg und Umbenennung des <i>SV Wilmersdorf</i>. (Beschluss der gemeinsamen Hauptversammlung am 9.April 1999). Dieser Tag kann als Gründungsdatum angenommen werden.
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (163, 'Schachclub Zitadelle Spandau 1977                        ', '1977 gegründet', '', '1977 kam es zu Unstimmigkeiten im [VERWEIS=116]Spandauer SV[/VERWEIS] wegen des Spiellokals. Trotz Warnungen einiger älterer Mitglieder, kam es zum Umzug in die ungemütlichen Räume der Zitadelle. Das führte zur Spaltung des Vereins und Gründung des Schachklubs Zitadelle.', 1977, 0, 'Berlin', 1);
