INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (178, 'Charlottenburger Schachgesellschaft von 1884', '14.11.1884 - mind. 1926', '', 'Im Mai/Juni 1885 zählte der Verein 28 Mitglieder. Vorsitzender war Oscar Cordel. Da das <a href="/archiv/bsz/0011-0020/index.html#bsz0015">1.Stiftungsfest</a> am 14.11.1885 gefeiert wurde, nehme ich an, das der Verein am 14.11. auch gegründet wurde.
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (179, 'Berliner Schachclub                         ', 'mind. 1884 - mind. 1884   ', '                                        ', 'Identisch mit dem Berliner Schachverein von 1876?<br>1885 war das Spiellokal die Academ.Bierhallen. Gespielt wurde Dienstag/Freitag. Der Klub hatte zu diesem Zeitpunkt 28 Mitglieder.', 0, 0, 'Berlin', 1);
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (180, 'Schachverein Germania                       ', 'mind. 1884 - mind. 1885   ', '                                        ', '1885 war der Spieltag Montag in der Dresdener Str.86. 20 Mitglieder.<br>Am 9.März 1885 fand im Vereinslokal Restaurant Teschner, Annenstr.1a, ein <a href="/archiv/bsz/0011-0020/index.html">Lösungsturnier (siehe BSZ 14)</a> statt.', 0, 0, 'Berlin', 1);
INSERT INTO `tl_vereinsregister` (id, name, timerange, url, info, foundingDate, resolutionDate, land, published) VALUES (181, 'Schachklub Springer', '1895 - mind. 1926', '', 'War am 26.04.1901 Gründungsmitglied des Allgemeinen Schachbundes zu Berlin. Ende 1905 hatte der Verein 161 Mitglieder und war einer der größten Vereine Deutschlands.
