INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (7, 1, 193808, 'Die Einzelmeisterschaft des Bezirks Norden errang in diesem Jahre R. Lehmann-Humboldt nach Stichkampf gegen Brandes-Moabit. Der vorjährige Bezirksmeister Silbermann-Pankow war aus beruflichen Gründen nicht in der Lage, seinen Titel zu verteidigen. — Nachstehend die Turnierreihenfolge: 1. Lehmann-Humboldt 8+1½, 2. Brandes-Moabit 8+½, 3.-4. Sternberg und Bochow — beide Pankow 6½, 5. Groneberg-Humboldt 6, 6.—7. Seiffert-Humboldt und Striedl-Reinickendorf 5, 8.—9. Redlich-Moabit und Hoose-Humboldt 4½. 10. Kater-Moabit 2, 11. Gronki-Weißensee 0.', 'BSV-Mitteilungsblatt August/September 1938', '', 1);
