INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (13, 3, 192002, 'Aus Charlottenburg. An der Schachabenden der Charlottenburger Turngemeinde herrscht reger Betrieb, durchschnittlich sind etwa 60 Spieler anwesend. An dem in drei Klassen stafffindenden Jugendschachturnier beteiligen sich 66 Spieler. In der letzten Zeit fanden drei Vorträge statt. Herr K.Schirm sprach über die französische Partie, Herr K.Pahl zeigte und erläuterte vorzügliche Kombinationen und der Schachmeister Herr Post fesselte seine Zuhörer durch einen sehr interessanten und lehrreichen Vortrag über das Bauernendspiel.', 'Deutsche Schachzeitung Nr.2 Februar 1920, S.41', '/archiv/bsz/0081-0090/index.html#bsz0082', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (14, 3, 192008, 'Aus Charlottenburg. In der Generalversammlung der Schachabteilung der Charlottenburger Turngemeinde wurde E.Hartmann zum Vorsitzenden gewählt, dem zur Unterstützung ein Ausschuß, bestehend aus den Vereinsmitgliedern Horn, Kirstein, Minikes und Schirm beigegeben wurde. Gegenwärtig trägt die Charlottenburger Turngemeinde gegen den Schachklub "Caissa", Magdeburg, sechs Fernpartien aus.', 'Deutsche Schachzeitung Nr.8/9 August/September 1920, S.196', '/archiv/bsz/0081-0090/index.html#bsz0082', 1);
