INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (30, 6, 192106, 'Meister Teichmann ist in diesen Tagen aus der Schweiz heimgekehrt. Das Schachheim hat einen Wettkampf von 6 Partien mit Aljechin veranstaltet. Die 1. u. 3. wurden schlicht, die 2. u. 4. gewann A.', 'Deutsches Wochenschach und Berliner Schachzeitung Nr.23/24 vom 12.Juni 1921, S.139', '', 1);
