INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (54, 10, 192105, 'Der Schachklub 1906 (Spar- u. Bauverein) hat nach dem Kriege einen erfreulichen Aufschwung genommen u. zählt z.Z. 32 Mitglieder. Die Di. u. Sd. stattfindenden Spielabende im Vereinszimmer des Hochschulbräus, Föhrer Str.10, erfreuen sich durchweg eines regen Besuches von Mitgliedern u. Gästen. Besondere Beachtung wird der reichhaltigen Vereinsbibliothek geschenkt.', 'Deutsches Wochenschach und Berliner Schachzeitung Nr.21/22 vom 29.Mai 1921, S.125', '', 1);
