INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (71, 11, 194911, 'Größere Schachveranstaltungen führte die Berliner Schachsparte am Bußtag durch. Im Norden spielten Tegel, Reinickendorf-Ost und -West sowie Reinickendorfer Füchse gegen Wedding/Humboldt 33:25. In Spandau blieb Eckbauer mit 22:16 erfolgreich. Im Süden siegte Kreuzberg 21½:12½ über Tempelhof-Mariendorf.', 'Deutsche Schachblätter Nr.12 Dezember 1949, S.190', '', 1);
