INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (94, 14, 19370530, '<b>Leipzig.</b> Berlins Mannschaftsmeister, Eckbauer - Charlottenburg, konnte am 30.Mai Leipzigs Städtemannschaft mit 16:14 schlagen. Ein schöner Erfolg der Berliner, die an den ersten 10 Brettern sogar mit 8:2 siegreich blieben. Hier waren Paarung und Ergebnis (Leipzig zuerst genannt): Schroeder 0 Kuppe, Röss 0 Jäschke, Walter 0 Vogt, Woog ½ Treumann, Riemann 0 Nowarra, Prof.Müller ½ Steneberg, Wojtech 0 Kipke, Krüger 0 Zybell, Stock 0 Kunerth, Starke 1 Prcewarcinski. Wie man sieht, kann Berlin also auch ohne seine Meister Erfolge erzielen.', 'Deutsche Schachblätter Nr.12, 15.Juni 1937, S.189', '', 1);
