INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (96, 14, 1938, '<b>Eckbauer Berlin-Charlottenburg.</b> Klubmeister für 1938 wurde in dem von 82 Teilnehmern bestrittenen Winterturnier Wilh.Kuppe mit 8 Punkten vor W.E.Kunerth 7½, Treumann 7½, Vogt 7, Steneberg 5 und Nowarra 4. 11 Teilnehmer. In Klasse Ia siegte Schneider, in Ib Sabo, in IIa Anton, in IIb Brendel, in III Rudnick.', 'Deutsche Schachblätter 1938, Anhang', '', 1);
