INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (201, 38, 193703, 'Die Schachgruppe C.Lorenz führt ein Frühjahrsturnier durch, in welchem 6 ihrer besten Spieler sich mit Czach, B.Koch, Rellstab und W.Schlage messen.', 'Deutsche Schachblätter Nr.8, 15.April 1937, S.128', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (202, 38, 193705, 'Im Frühjahrsturnier der Schachgruppe C.Lorenz siegte Rellstab mit 8 Punkten vor Czach und B.Koch je 6, Schlage 5½, Brosow und Szczepaniak je 4½, Putz 4, Fröhlich 3, Hilgers 2½, Haum 1.', 'Deutsche Schachblätter Nr.12, 15.Juni 1937, S.190', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (203, 38, 193802, '<b>Magdeburg.</b> Die Schachabteilung der Betriebssportgemeinschaft Lorenz Berlin war vom Berliner Schachverband mit der Vertretung Berlins in einem Städtekampf gegen Magdeburg betraut worden. Sie siegte knapp mit 13:12 Punkten. An den ersten vier Brettern waren Brosow (g.Kannemann), Czach (g.Roth), B.Koch (g.Buchholz) und Szczepaniak (g.Hopp) für Berlin erfolgreich.', 'Deutsche Schachblätter Nr.5, 1.März 1938, S.78', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (204, 38, 194001, 'Die Mannschaftskämpfe haben mit Rekordbeteiligung begonnen. In der ersten Klasse steht ein spannender Zweikampf Eckbauer - Schachgesellschaft in Aussicht, doch sind auch die Mannschaften von Humboldt und Lorenz gefährlich.', 'Deutsche Schachblätter Nr.3/4, 1.Februar 1940, S.30', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (205, 38, 194006, 'Kontakt: Hermann Gulweida, Berlin-Tempelhof, Rohdestr. 18, 754387, Donnerstag Kameradschaftsheim Lorenz Bln.-Mariendorf, Zastrowstr.', 'BSV-Mitteilungsblatt Juni 1940', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (206, 38, 194006, 'Meisterschaft von Berlin: Die Kriegsmeisterschaft des Berliner Schachverbandes ist allen Schwierigkeiten zum Trotz glatt und ohne jede Störung verlaufen. Es nahmen diesmal nur die Sieger der Bezirksturniere teil, von den Meistern fehlten vor allem Rellstab und Richter. Das Turnier entwickelte sich zu einen sehr scharfen, spannenden und wechselvollen Kampfe. Lange Zeit führte Grammatikoff, bis kurz vor Schluß konnten seine stets zahlreich als Zuschauer erschienenen Bezirkskameraden auf seinen Sieg hoffen. Knapp vor dem Ziel aber wurde sein Siegeslauf jäh unterbrochen und der anfangs kaum beachtete Palme, der aus den ersten 5 Partien nur 2 Punkte erzielt hatte, gelangte mit erheblichem Vorsprung an die Spitze. Mit diesem großen Erfolge hat Palme eine erhebliche Formverbesserung unter Beweis gestellt. Schon in den vorhergehenden Bezirks- und Mannschaftskämpfen zeigte Palme sich als kommender Mann; in der Berliner Meisterschaft wuchs er förmlich von Runde zu Runde heran, er war in der zweiten Hälfte des Turniers allen Mitbewerbern überlegen, sein Sieg ist einwandfrei und zu Recht errungen. Der Schlußstand ist: 1. Palme (Eckbauer) 8 Punkte, 2. bis 5. Czach (Lorenz), Dahl (Neukölln), Grammatikoff (Pankow) und Nowarra (Eckbauer) 6½ Punkte, 6. Sämisch (Schachgesellschaft) 6 Punkte, 7. bis 8. Hohensee (Lichtenberg) und B. Koch (Lorenz) 5½ Punkte, 9. Halosar (Schachgesellschaft) 5 Punkte, 10. Teschner (Schöneberg) 4 Punkte, 11. Lehmann (Humboldt) 3 Punkte, 12. Utzat (Kreuzberg) 2½ Punkte. Das Turnier wurde von Kurt Richter in bester Weise geleitet.', 'BSV-Mitteilungsblatt Juni 1940', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (207, 38, 19411106, 'Die Betriebsschachgruppe C.Lorenz konnte am 6.November auf ein 10jähriges Bestehen zurückblicken. Im Jubiläumsturnier siegte überraschend Szczepaniak vor B.Koch und Brosow. Ein Wettkampf gegen die Ortsgruppe Wilmersdorf endete ganz knapp mit 21:20 Punkten für Lorenz.', 'Deutsche Schachblätter Nr.23/24, 1.Dezember 1941, S.190', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (208, 38, 1942, '<b>Lorenz:</b> Hermann Gulweida, Berlin-Tempelhof, Rohdestr. 18, 754387, Donnerstag Kaffee Viktoria.', 'BSV-Mitteilungsblatt Oktober 1942', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (209, 38, 194203, 'In der Berliner Mannschaftsmeisterschaft gab es in diesem Jahre einen offenen Kampf zwischen den Spitzenvereinen, da die sieggewohnte Berliner Schachgesellschaft ohne Richter, der im Wehrdienst steht, und ohne Sämisch, der sich zur Wehrmachtbetreuung in Südfrankreich befindet, antreten mußte. Die Titelverteidigerin siegte jedoch wiederum mit 34 Punkten vor Eckbauer 31½, Humboldt 28½, Lorenz 26, Wilmersdorf 22, Cottbusser Tor 8 Punkte.', 'Deutsche Schachblätter Nr.7/8 vom 1.April 1942, S.59', '/archiv/bsz/0081-0090/index.html#bsz0090', 1);
