INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (247, 43, 190105, 'Der Schachverein "Nord" hat sich aufgelöst.', 'Deutsche Schachzeitung Nr.5 Mai 1901, S.154', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (248, 43, 190106, 'Die im Maiheft gebrachte Notiz über die Auflösung des Schachclubs "Berlin-Nord" beruht auf einer irrthümlichen Berichterstattung. Der Club besteht nach wie vor fort.', 'Deutsche Schachzeitung Nr.6 Juni 1901, S.184', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (249, 43, 190206, 'Herr K.Nebel, ein hervorragendes und verdientes Mitglied des Berlin Nord, ist vor kurzem verstorben.', 'Deutsches Wochenschach und Berliner Schachzeitung Nr.27 6.Juli 1902, S.224', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (250, 43, 19050429, 'Der Allgemeine Schachbund, welcher die "Berliner Schachgesellschaft", den "Berliner Schachverein", Schachklub "Springer", Schachklub "Turm", Schachklub "Anderssen", Schachklub "Berlin-Nord", Schachklub "Walbrodt" und "Rixdorfer Schachverein" umfaßt, hält auch in diesem Jahre ein Turnier ab, das am 29.April beginnt. Das Turnier ist für sämtliche Bundesmitglieder offen und wird diesmal, um auch schwächeren Spielern Gelegenheit zur Teilnahme und Aussicht auf Erfolg zu geben, in vier Klassen von höchstens 12 Teilnehmern gespielt; bei Mehranmeldungen werden weitere Abteilungen gebildet. Gespielt wird Dienstag und Sonnabend abends im Königstadt-Kasino, Alexanderstraße 21 (am Stadtbahnhof Jannowitzbrücke).', 'Deutsche Schachzeitung Nr.4 April 1905, S.123/124', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (251, 43, 19050928, 'Der "Allgemeine Schachbund" hielt am 28.September im Lokal des Berliner Schachvereins seine Mitgliederversammlung ab. Den Jahresbericht erstattete Herr W.Pape. Der Bund zählt gegenwärtig 396 Mitglieder, welche sich wie folgt verteilen: 1 Gönner (J.Bierbach), Schachgesellschaft 131, "Springer" 113, "Turm" 45, Schachverein 36, "Anderssen" 24, "Walbrodt" 18, "Nord" 15, Rixdorfer Schachverein 13. In den Bundesvorstand wurden gewählt: 1.Vorsitzender W.Pape, 2.Vorsitzender F.W.Leonhardt, 3.Vorsitzender F.Bretschneider, 1.Schriftführer Professor Simon, 2.Schriftführer H.Friedrich, 1.Schachwart Dr.Böhmer, 2.Schachwart A.Wagner. Als Schatzmeister fungieren wie bisher weiter J.Reichenheim und H.Ranneforth.
Für den Winter ist ein Turnier für erstklassige Spieler mit Gesamtpreisen von 600 M in Aussicht genommen. Das Turnierkomitee besteht aus den Herren W.Pape, F.W.Leonhardt, Dr.M.Lewitt, H.Spieß und Professor Simon.', 'Deutsche Schachzeitung Nr.10 Oktober 1905, S.314', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (252, 43, 190609, 'Der "Berliner Schachverein von 1876" hat sich mit dem Schachklub "Berlin-Nord" vereinigt und hält nunmehr wöchentlich zwei Spielabende ab: am Donnerstag im Hotel Krebs (Niederwallstraße 11) und am Montag in den Johannissälen (Johannisstraße 20).  Das Winter- turnier, welches Mitte Oktober begann und in drei Klassen gespielt wird, findet in beiden Lokalen statt.', 'Deutsche Schachzeitung Nr.10 Oktober 1906, S.314/315', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (253, 43, 19060924, 'Am 24. September hielt der "Allgemeine Berliner Schachbund" in den Räumen des Schachklubs "Nord" seine Hauptversammlung ab. Herr W. Pape erstattete den Jahresbericht, aus welchem zu entnehmen ist, daß der Bund, welcher im letzten Jahre 109 neue Mitglieder gewonnen hat, jetzt über 500 Mitglieder zählt.  Die ausscheidenden Vorstandsmitglieder Herren F. W. Leonhardt (2. Vorsitzender), H. Ranneforth (2. Schatzmeister) und Dr. Böhmer (Schachwart) wurden durch Zuruf wiedergewählt.  Herr Th. Bretschneider wurde als Vertrauensmann des Bundes im Verkehr mit dem Vorstande des Deutschen Schachbundes bestimmt.', 'Deutsche Schachzeitung Nr.10 Oktober 1906, S.314', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (254, 43, 190610, 'Der "Berliner Schachverein von 1876" hat sich mit dem Schachklub "Berlin-Nord" vereinigt und hält nunmehr wöchentlich zwei Spielabende ab: am Donnerstag im Hotel Krebs (Niederwallstraße 11) und am Montag in den Johannissälen (Johannisstraße 20).', '', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (255, 43, 19071120, 'Am 20.November wurde im Königstadt-Kasino (Alexanderstraße 21) der diesjährige Massenwettkampf des Allgemeinen Berliner Schachbundes abgehalten. Es kamen 75 Partien zustande. Auf der einen Seite (Rote Rose) kämpften die Vereine "Springer", Schachverein, "Pythagoras" und "Nord", auf der anderen Seite (Weiße Rose) "Schachgesellschaft", "Anderssen", "Turm", "Walbrodt", die Klubs Spandau, Wildenschaft, Bauer, Reinickendorf, Spar- und Bauverein, Rixdorf und Gäste. Die Rote Rose gewann mit 41:34 (im Vorjahre 45:26).', 'Deutsche Schachzeitung, Nr.12 Dezember 1907, S.377', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (256, 43, 190811, 'Im diesjährigen Massenwettkampf des Allgemeinen Schachbundes kamen 106 Partien zustande, von welchem die "Rote Rose" 52, die "Weiße Rose" 42 gewann; 12 Partien blieben unentschieden. Im einzelnen ergaben sich auf Seite der Sieger: Springer 36,23,7, Schachverein 6,7,3, Nord 4,2,2, Pythagoras 4,3,0, Spandauer Schachgesellschaft 2,2,0, Gäste 0,5,0; auf Seite der Unterlegenen: Schachgesellschaft 17,11,4, Turm 7,6,0, Anderssen 4,5,1, Wildenschaft 3,5,4, Spar- und Bauverein 6,10,0, Walbrodt 1,1,1, Bauer 1,7,1, Rixdorf 3,5,1, Gäste 0,2,0. Die schwierige Leitung des Unternehmens lag in den Händen von W.Pape und R.Steinweg.', 'Deutsche Schachzeitung, Nr.12 Dezember 1908, S.380', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (257, 43, 191103, 'Der Schachklub "Nord" hielt Ende März seine Generalversammlung im Klublokal "Schachklause", Restaur.Döring, Linienstr.142 (Oranienburger Tor) ab. Es wurde u.a. beschlossen, die Fusion mit dem Berliner Schachverein von 1876 zu lösen und ferner aus dem Deutschen Schachbunde auszutreten. In den Vorstand wurden wiedergewählt: Ed.Schermenske, Vors.; G.Friebe, Schriftführer.', 'Deutsche Schachzeitung, Nr.5 Mai 1911, S.153', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (258, 43, 191410, 'Herr Karl Doppler, ein bekannter Berliner Schachfreund, ist kürzlich gestorben. Er war viele Jahre Mitglied des Schachklubs "Nord" und beteiligte sich mit Erfolg an mehreren Turnieren des Deutschen Schachbundes, dessen Mitgbegründer er war.', 'Deutsche Schachzeitung, Nr.11 November 1914, S.347', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (259, 44, 18981101, '<b>Berlin.</b> Ein neuer Schachverein hat sich am 1. November unter dem Namen "Neuer Berliner Schachclub" gebildet; derselbe hält seine Sitzungen jeden Freitag Abend im "Münzhof" (Kaiser Wilhelmstr.) ab.', 'Gefunden von Werner Berger in Der Schachfreund, Dezember 1898, S. 159', '', 1);
