INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (280, 46, 193102, 'Der Schachklub Bauer E.V. Berlin, gegr. 1901, begeht am 22. Februar, 20 Uhr, in seinen Klubräumen Frankfurter Hof, Frankfurter Allee 313, das Jubiläum seines 30jährigen Bestehens. Das Jubiläumsturnier beginnt am 18. Februar, 20 Uhr.', 'Deutsche Schachzeitung 1931, S.57 / Alan McGowan', '', 1);
