INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (315, 47, 1926, '<b>Schachklub Walbrodt 1896</b>; korporatives Mitglied des Berliner Handwerkervereins; N 54, Sophienstr. 18; Do.; Vors. Kurt Krispin; Schriftf. Max Dobers, Wattstr. 9; Schatzm. Max Kreuter.', 'Ranneforths Schachkalender 1926, S.149-152', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (316, 48, 1906, 'Im Sommer d. J. wurden neu gegründet: ein Schachklub in Reinickendorf bei Berlin, welcher seine Versammlungen Dienstags im Restaurant Bürgerhaus abhält.', 'Deutsche Schachzeitung Nr.11 November 1906, S.350', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (317, 48, 19071120, 'Am 20.November wurde im Königstadt-Kasino (Alexanderstraße 21) der diesjährige Massenwettkampf des Allgemeinen Berliner Schachbundes abgehalten. Es kamen 75 Partien zustande. Auf der einen Seite (Rote Rose) kämpften die Vereine "Springer", Schachverein, "Pythagoras" und "Nord", auf der anderen Seite (Weiße Rose) "Schachgesellschaft", "Anderssen", "Turm", "Walbrodt", die Klubs Spandau, Wildenschaft, Bauer, Reinickendorf, Spar- und Bauverein, Rixdorf und Gäste. Die Rote Rose gewann mit 41:34 (im Vorjahre 45:26).', 'Deutsche Schachzeitung, Nr.12 Dezember 1907, S.377', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (318, 49, 19070120, 'Am 20.Januar wurde hier der Schachklub "Pythagoras" gegründet, welcher bereits mehr als 20 Mitglieder zählt und jeden Donnerstag Abend im vegetarischen Restaurant und Café (Königstraße 29, I.) seine regelmäßigen Zusammenkünfte abhält. Der Vorstand besteht aus den Herren: O.Wieck, Vorsitzender; A.Schlößer, Vorsitz.-Stellvertreter; P.Piotrowsky, Schriftführer; K.Imke, Schachwart; H.Turconi, Bücherwart; G.Steinhoff, Schachwart.', 'Deutsche Schachzeitung, Nr.3 März 1907, S.91', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (319, 49, 19071120, 'Am 20.November wurde im Königstadt-Kasino (Alexanderstraße 21) der diesjährige Massenwettkampf des Allgemeinen Berliner Schachbundes abgehalten. Es kamen 75 Partien zustande. Auf der einen Seite (Rote Rose) kämpften die Vereine "Springer", Schachverein, "Pythagoras" und "Nord", auf der anderen Seite (Weiße Rose) "Schachgesellschaft", "Anderssen", "Turm", "Walbrodt", die Klubs Spandau, Wildenschaft, Bauer, Reinickendorf, Spar- und Bauverein, Rixdorf und Gäste. Die Rote Rose gewann mit 41:34 (im Vorjahre 45:26).', 'Deutsche Schachzeitung, Nr.12 Dezember 1907, S.377', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (320, 49, 190808, '<b>Charlottenburg.</b> Die Schachabteilung der Wildenschaft beendete ihr viertes Semester. Aus dem Sommerturnier, das diesmal in Gruppen gespielt wurde, deren Sieger zu einem Hauptturnier zusammentraten, ging stud. iur. Rudolf Dührssen als erster Sieger hervor und wiederholte damit seinen glänzenden Erfolg aus dem Winterturnier. Den 2. und 3. Preis teilten J.Feingold-Doré und Ed.Lasker, 4.Preisträger L.Vogel, 5.Simonowitz. Die im Gruppenturniere ausgeschiedenen 14 Teilnehmer bestritten ein Nebenturnier, welches Hugo Obst gewann. Der Wettkampf mit dem Schachklub Pythagoras wird erst im November stattfinden. In der Mitgliederversammlung wurde Willy Kretzschmar, stud. ing., wiederum zum Vorsitzenden gewählt und beschlossen, im Winter ein Vorgabeturnier zu veranstalten, Beginn am 3.November.', 'Deutsches Wochenschach und Berliner Schachzeitung Nr.33 vom 16.August 1908, S.301', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (321, 49, 190811, 'Im diesjährigen Massenwettkampf des Allgemeinen Schachbundes kamen 106 Partien zustande, von welchem die "Rote Rose" 52, die "Weiße Rose" 42 gewann; 12 Partien blieben unentschieden. Im einzelnen ergaben sich auf Seite der Sieger: Springer 36,23,7, Schachverein 6,7,3, Nord 4,2,2, Pythagoras 4,3,0, Spandauer Schachgesellschaft 2,2,0, Gäste 0,5,0; auf Seite der Unterlegenen: Schachgesellschaft 17,11,4, Turm 7,6,0, Anderssen 4,5,1, Wildenschaft 3,5,4, Spar- und Bauverein 6,10,0, Walbrodt 1,1,1, Bauer 1,7,1, Rixdorf 3,5,1, Gäste 0,2,0. Die schwierige Leitung des Unternehmens lag in den Händen von W.Pape und R.Steinweg.', 'Deutsche Schachzeitung, Nr.12 Dezember 1908, S.380', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (322, 50, 19080109, '<b>Aus Treptow (bei Berlin).</b> Der am 14.Februar 1907 gegründete Schachklub "Oberspree" hielt am 9.Januar seine erste ordentliche Hauptversammlung ab. In derselben wurde der bisherige Vorstand durch Zuruf wiedergewählt. Für das laufende Jahr führen demnach die Geschäfte des Vereins: Kaufmann Schröder, I.Vorsitz.; Rentier Hagelweide, II.Vorsitz., und Landesobersekretär Mittag, Schriftführer und Schatzmeister. Der Verein zählt derzeit 15 Mitglieder. Seine Spielabende hält er jeden Montag und Donnerstag im Restaurant "Zum Kurfürst" (Behringstraße 1) ab.', 'Deutsche Schachzeitung, Nr.2 Februar 1908, S.63/64', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (323, 50, 19080514, '<b>Treptow (bei Berlin).</b> Am 14.Mai spielte Herr W.Schlage aus Berlin im hiesigen Schachklub "Oberspree" 12 Partien simultan, von denen er in 3½ Stunden 9 gewann, 1 verlor und 2 remis machte.', 'Deutsche Schachzeitung, Nr.6 Juni 1908, S.191', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (324, 50, 19190107, '<b>Berlin-Treptow.</b> Heute hielt der Schachklub Oberspree seine 24. geschäftliche Sitzung - 9.Hauptversammlung - im Wirtshaus L.Pinnow, Baumschulenstraße 102, ab. Da seit dem 23.Januar 1915 keine Sitzung mehr stattgefunden hatte, wurden die Jahres- und Kassenberichte für 1915 bis einschließlich 1918 vorgelegt. Während die Berichte für 1915/17 den hemmenden Einfluß des Krieges deutlich erkennen ließen, ging aus dem Bericht für 1918 hervor, daß in diesem Jahre die Vereinstätigkeit wieder aufgelebt ist. Außer einem Sommer-Vorgabe-Turnier wurden ein Blitz-Turnier, ein Scherz-Turnier, ein Simultanspiel und ein Blindspiel veranstaltet. In den Vorstand wurden gewählt die Herren Mittag (1.Vors.), Petrusch (2.Vors.), Zülsdorf (Schriftf.), Höregott (kass.) und Barth (Schachw.).', 'Deutsches Wochenschach und Berliner Schachzeitung Nr.5/6 vom 5.Februar 1919, S.36/37', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (325, 50, 19190224, '<b>Berlin-Treptow.</b> Heute beging der Schachklub Oberspree im Wirtshaus Pinnow unter starker Beteiligung der Mitglieder und in Anwesenheit einiger Gäste sein 12.Stiftungsfest. Es zerfiel in 3 Teile: Wurstessen (!), Blitzturnier und Fidelitas. Der Vorsitzende Herr P.Mittag hob in seiner Begrüßungsansprache hervor, daß der Verein zum ersten Mal seit Bestehen sein Stiftungsfest wirklich am Stiftungstag feiern könne, gedachte der früheren Festtage und gab der Hoffnung auf bessere Zeiten Ausdruck. Der 2.Vorsitzende Herr Petrusch überreichte dem Vorsitzenden in Anerkennung seiner Verdienste um den Verein im Namen der Mitglieder das Cordelsche Werk "Theorie und Praxis des Schachspiels", eine Aufmerksamkeit, wofür der ganz überraschte Vorsitzende herzlich dankte. Von auswärtigen Mitgliedern waren aus Siemensstadt und Düsseldorf Glückwünsche eingelaufen. Im Blitzturnier errangen Preise die Herren Winter, Busse, Höregott, Krause, Hofmeister, Mittag, Petrusch, Kraushaar und Kablau. Die dann folgende Fidelitas brachte Vorträge aller Art und machte den Abend so genußreich, wie es in dieser Zeit eben möglich war.', 'Deutsches Wochenschach und Berliner Schachzeitung Nr.9/10 vom 9.März 1919, S.61', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (326, 50, 19190325, 'Zu der Zusammenkunft in der Berliner Schachgesellschaft am 25.März hatten sich Vertreter von 17 Groß-Berliner Schachvereinen eingefunden. Der Vorsitzende der Schachgesellschaft, Herr Amtsanwalt Post, legte die Notwendigkeit und Zweckmäßigkeit eines Zusammenschlusses dar. Es solle jedoch nicht ein fester Bund mit Zwang und Pflichten, die die Selbständigkeit und das Eigenleben der Vereine beeinträchtigen, gebildet werden, sondern eine freie Gemeinschaft, an deren Veranstaltungen jeder Verein nach dem jeweiligen Grade seines Interesses teilnehmen könnte. Gedacht sei an Vereinswettkämpfe in Mannschaftsform und durch Beratungspartien, an die gemeinsame Veranstaltung von Wettbewerben nach Art des kürzlich beendeten Turniers um die Meisterschaft von Berlin, an Städte- und Massenkämpfe und an die gegenseitige Unterstützung bei den besonderen Unternehmungen der Vereine. Die Berliner Schachgesellschaft sei bereit, ihre Räume und ihre Mittel in den Dienst der Sache zu stellen. Eine Anfrage aus der Versammlung nach dem Bestehen des Berliner Schachbundes beantwortete der Vorsitzende des Berliner Schachvereins, Herr Wagner, dahin, daß man den Bund für erloschen halten müsse, nachdem er seit Jahren kein Lebenszeichen mehr gegeben. Auf eine weitere Frage über den Deutschen Schachbund erklärte Herr Amtsanwalt Post es für sehr wünschenswert, daß alle Groß-Berliner Schachvereine sich dem Deutschen Schachbunde anschlössen, wonach dann die neu zu gründende Vereinigung als Landesverband im Bunde gelten könnte. Voraussetzung müsse allerdings sein, daß der Deutsche Schachbund stärker als früher das nationale Schach begünstige und mehr für die Förderung des deutschen Nachwuchses und das Leben in den Vereinen sorge. Die Versammelten stimmten diesen Ausführungen zu und versprachen, in ihren Vereinen den Beitritt zum Deutschen Schachbunde zu betreiben. Es wurde dann einstimmig die Gründung eines Zusammenschlusses unter dem Namen: "Freie Vereinigung der Groß-Berliner Schachvereine" beschlossen. Es traten bei: Schachverein Anderssen, Schachverein Bauer, Berliner Schachgesellschaft von 1827, Berliner Schachverein von 1876, Schachabteilung des Sportklubs Charlottenburg, Schachklub Doppelbauer, Schachklub Springer, Schachklub Süd-West, Schachklub Walbrodt, Schöneberger Schachklub, Schachverein Schallopp in Steglitz, Schachgesellschaft Steglitz-Südende, Schachverein Lichterfelde, Mariendorfer Schachklub, Schachklub Oberspree, Schachklub Pankow und Karlshorster Schachgesellschaft, zusammen 17 Vereine mit 850 Mitgliedern.
.......', 'Deutsches Wochenschach und Berliner Schachzeitung Nr.14/15 vom 13.April 1919, S.98/99', '/archiv/bsz/0081-0090/index.html#bsz0088', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (327, 50, 19190426, '<b>Treptow-Baumschulenweg.</b> Heute fand hier ein Wettkampf des Schachklubs Oberspree mit dem Neuköllner Schachverein statt. Neukölln erzielte 6½, Oberspree 5½ Gewinnpunkte.', 'Deutsches Wochenschach und Berliner Schachzeitung Nr.18/19 vom 11.Mai 1919, S.124', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (328, 50, 191906, 'Freie Vereinigung: In nächster Zeit fanden bzw. finden folgende Vorträge statt: 13.Juni in Oberspree: E.Post über Bauernendspiele; 18.Juni in Tegel: W.Schlage über Königsgambit; 19.Juni in Neukölln: E.Post über Spanische Partie; 20.Juni in Schöneberg: Dr.Lewitt über Gambitspiele; 24.Juni im Doppelbauer: E.Post über Mittel- und Evansgambit. Simultanvorstellungen sind bzw. waren am 17.Juni in Karlshorst (E.Post) und am 2.Juli im Sportklub Charlottenburg (Sämisch). Am 27.Juni treffen sich sämtliche Groß-Berliner Vereine auf Einladung der Karlshorster Schachgesellschaft um 7 Uhr auf dem Bahnhof in Sadowa; von dort Spaziergang nach Karlshorst, Rheinischer Hof (am Bahnhof); dort ab 8 Uhr freies Turnier.
In den Vereinen ist zurzeit ein Lösungsturnier von Dr.Lewittschen Aufgaben im Gange.', 'Deutsches Wochenschach und Berliner Schachzeitung Nr.24/26 vom 22.Juni 1919, S.164', '/archiv/bsz/0081-0090/index.html#bsz0088', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (329, 50, 191906, 'In der Freien Vereinigung der Groß-Berliner Schachvereine sind die Mannschaftskämpfe um den von der Berliner Schachgesellschaft gestifteten Wanderpreis in vollem Gange. Es beteiligen sich 14 Vereine mit 27 Mannschaften. Die Ergebnisse der 3.Klasse waren bisher: Doppelbauer 8 Punkte gegen Tegel 2 und A.E.G. 2, Sportklub Charlottenburg 8 gegen Schiller-Realgymnasium 2, Oberspree 4 und Karlshorst 3½ gegen Süd-West 2½ und Neukölln 2, Doppelbauer 9 gegen Sportklub Charlottenburg 3, Springer 5 gegen Neukölln 3 und A.E.G. 1, Schachgesellschaft 6½ gegen Oberspree 4½ und Karlshorst 1. In der 2.Klasse erzielten: Schachverein 6 gegen Springer 3 und Bauer 3, Schachverein 4½ gegen Neukölln 4½ und A.E.G. 0, Schachgesellschaft 7 gegen Anderssen 3 und Tegel 1. Die nächsten Wettspiele fanden bzw. finden statt: 1.Klasse am 13.Juni in der Schachgesellschaft; 2.Klasse am 18.Juni, 24.Juni und 1.Juli in der Schachgesellschaft, 16.Juni im Schachverein, 20.Juni in Süd-West und 2.Juli im Bauer; 3.Klasse 17.Juni im Doppelbauer, 18.Juni in A.E.G., 19.Juni und 30.Juni im Schachverein, 20.Juni, 25.Juni und 3.Juli in der Schachgesellschaft, 3.Juli in Neukölln und 4.Juli im Springer.', 'Deutsches Wochenschach und Berliner Schachzeitung Nr.24/26 vom 22.Juni 1919, S.163/164', '/archiv/bsz/0081-0090/index.html#bsz0088', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (330, 50, 19190613, 'Ehrhardt Post gibt beim SK Oberspree einen Vortrag über Bauernendspiele.', '', '/archiv/bsz/0081-0090/index.html#bsz0088', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (331, 50, 19200123, 'Der Schachklub Oberspree hielt seine 10.Hauptversammlung ab. Der Jahresbericht entrollte ein günstiges Bild. Noch nie, selbst vor dem Kriege nicht, war das Schachleben im Klub so rege wie im abgelaufenen Geschäftsjahre. Es kann das wohl zum großen Teil auf den Anschluß des Klubs an die "Freie Vereinigung Groß-Berliner Schach-Vereine" zurückgeführt werden. In den Vorstand wurden wieder- bzw. neugewählt die Herren Mittag (1.Vors.), Petrusch (2.Vors.), Görler (Schriftf.), W.Rahn (Kass.) und Barth (Schachw.).', 'Deutsche Schachzeitung Nr.2 Februar 1920, S.41', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (332, 50, 19210118, '<b>Berlin-Treptow</b>. Der Schachklub Oberspree hielt im Wirtshaus P.Walz, Baumschulenstr.102, seine Hauptversammlung ab. Jahres- u. Kassenbericht lassen nach jeder Richtung hin Fortschritte erkennen. In den Vorstand wurden die Herren Mittag, Petrusch, Mielke, W.Rahn u. Koch gewählt. Zurzeit ist ein Turnier um die Klubmeisterschaft (16 Teiln.) für 1921 im Gange.', 'Deutsches Wochenschach und Berliner Schachzeitung Nr.5/6 vom 6.Februar 1921, S.30', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (333, 50, 19210621, '<b>Berlin-Treptow.</b> Das Turnier um die Meisterschaft des Schachklubs Oberspree für 1921 hat sein Ende erreicht. Heute fand die Preisverteilung statt bei einem aus diesem Anlaß veranstalteten Fest, das mit Tanz und Vorträgen einen harmonischen Verlauf nahm. Der 1.Preis und die Klubmeisterschaft fielen an Hrn. Höregott; den 2.Preis der Ia-Klasse erhielt Hr.Mittag, der vorj.Klubmeister. In der Ib-Klasse errangen den 1.Preis Hr.Lorber, den 2. Hr.Krause und den 3. Hr.Seiler. Von den Spielern der II.Klasse erhielten Hr.Koch den 1. und Hr.Fiedler den 2.Preis. Neben dem Turnier fand noch ein kleiner Wettkampf zwischen den Herren P.Schmiechen u. Stuber statt, aus dem ersterer mit 5:3 Punkten als Sieger hervorging. Die Preise bestanden in Schachwerken, Schachbrettern und Schachnadeln.', 'Deutsches Wochenschach und Berliner Schachzeitung Nr.27/28 vom 10.Juli 1921, S.166', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (334, 50, 19220609, 'Der neugegründete Berliner Schachverband veranstaltete der Schöneberger Schachklub 1913 in seinem Klubheim ein "Massenturnier mit Preisverlosung", bei dem leider die "Massen" fehlten; denn außer 31 Schönebergern waren nur 15 Gäste anwesend, die sich aus den Vereinen Friedenau, Neukölln, Anderssen, Oberspree und Berliner Schachgesellschaft eingefunden hatten.', 'Deutsche Schachblätter Nr.7 15.Juli 1922, S.111', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (335, 50, 19220623, '<b>Berlin-Treptow</b>. Der Schachklub Oberspree hat sein Spiellokal verlassen müssen, weil das Wirtshaus Wälz seine Pforten geschlossen hat. Die Spielabende finden seit heute Dienstags und Freitags in der Bahnhofsklause, Baumschulenstraße 100, Ecke Sernstraße, statt.', 'Deutsche Schachblätter Nr.7 15.Juli 1922, S.111', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (336, 50, 19221028, '<b>Berlin-Treptow.</b> Der Schachklub "Oberspree" veranstaltete zu Ehren seines vor einem Jahre verstorbenen Mitgliedes Rechnungsrat Blume ein "Willy Blume-Gedächtnis-Turnier". Heute fand die Verteilung der Preise, zu deren Beschaffung zwei Klubfreunde des Verstorbenen namhafte Beträge gestiftet hatten, im Klublokal statt: 1. Lorber, 2. Höregott, 3. Mittag, 4.-6. Martens, A. Rahn u. Zülsdorff. Die Witwe des Verewigten hat der Vereinsbibliothek zum Andenken an ihren Gatten ein Schachwerk überwiesen.', 'Deutsche Schachblätter Nr.11 15.November 1922, S.181', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (337, 50, 19221128, 'Treptow: Schachmeister Willi Schlage gab im Schachklub Oberspree (Klublokal "Restaurant zur Bahnhofsklause") eine Reihenspielvorstellung an 25 Brettern mit dem Ergebnis: in 3½ Stunden + 21 = 4 (gegen Hohensee, Mutschke, Groeper und Koffka).', 'Deutsche Schachblätter Nr.12 15.Dezember 1922, S.198', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (338, 50, 1925, 'Der SK Oberspree gehört zum Berliner Schachverband.', 'Ranneforths Schachkalender 1925, S.118/119', '', 1);
