INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (446, 62, 19220926, 'Leider sah sich der Schöneberger Schachklub heute genötigt, sein Klubheim zu verlegen. Er spielt jetzt jeden Dienstag und Freitag im Albertshof, Albertstr.15, Ecke Feurigstraße. Gäste sind jederzeit willkommen.', '', '', 1);
