INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (466, 62, 193806, 'Die Schachgruppe Schöneberg beging im Juni ihr 25jähriges Bestehen. Aus diesem Anlaß wurde zu Ehren des Gründers ein "Albert Kelm-Jubiläumsturnier" ausgespielt, das 24 Teilnehmer bestritten. Sieger in diesem Turnier war der jugendliche Rudolf Teschner, der von 23 Partien 21,5 Punkte ohne Verlust erreichte. Ferner wurde ein Blitzturnier in drei Abteilungen ausgetragen. Der bekannte Problemkomponist Witwer, der ein langjähriges Mitglied der Schöneberger Gruppe ist, widmete dem Verein einen prächtigen 4-Züger. An einer Simultanveranstaltung, die Meister Schlage gab, gewann dieser 14 Partien und machte zwei remis.', 'BSV-Mitteilungsblatt August/September 1938', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (467, 62, 19380731, 'Am Sonntag, dem 31. Juli, veranstaltete der Bezirk Osten im "Orpheum" (früher Happolds), Berlin, Hasenheide, ein Schachpropagandaspiel. In zwei Klassen ging es "blitzmäßig" zu. Herr Röpstorff (Gruppe Baumschulenweg), Teschner (Schöneberg) und Post (Baumschulenweg) waren die drei Besten in der 1. Klasse. Eine Beratungspartie zwischen Neukölln und Oberschöneweide wurde von Neukölln gewonnen.', 'BSV-Mitteilungsblatt August/September 1938', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (468, 62, 193808, 'Zwischen den Gruppen Steglitz - Schöneberg - Kreuzberg - Lichterfelde - Zehlendorf - Tempelhof und Klein-Machnow wird seit einiger Zeit ein Bezirks-Sommerturnier ausgetragen. Der Stand ist augenblicklich Kreuzberg 28, Schöneberg 24.', 'BSV-Mitteilungsblatt August/September 1938', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (469, 62, 1939, '<b>Großdeutscher Schachbund, Ortsgruppe Schöneberg</b> Vors.: Friedel, Martin-Luther-Str.84', 'Berliner Adressbuch von 1939', '', 1);
