INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (492, 63, 19411206, '<b>Prof.Johannes Oehquist</b>, der bekannte finnische Dichter und Kulturhistoriker, der sein ganzes Leben der Vertiefung der deutsch-finnischen Beziehungen gewidmet hat, zeitweise als Lektor der deutschen Sprache an der Universität Helsingfors und als Lektor der finnischen Sprache in Berlin, später als Kulturattaché der Berliner finnischen Gesandtschaft, feierte am 6.Dezember seinen 80.Geburtstag. Er war in der Nähe von Petersburg als Sohn eines finnischen Pfarrers geboren und während des Weltkrieges von der zaristischen Regierung zum Tode verurteilt. 1938 machte er sich durch sein auch ins Deutsche übersetzte Buch "Das Reich des Führers" zum Anwalt des Nationalsozialismus in Finnland.
Das Schachspiel schätzte Oehquist als erfolgreichen Problemdichter, aber auch als praktischen Spieler. Auf seine Anregung wurde 1919 die Friedenauer Schachgesellschaft gegründet, die er auch einige Zeit geleitet hat.', 'Deutsche Schachzeitung, Nr.12 Dezember 1941, S.180', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (493, 63, 1942, '<b>Friedenau:</b> Carlheinz Riepenhausen, Bln.-Steglitz, Menckenstr. 11, Montag und Donnerstag, Gaststätte "Zum Dürer", Friedenau, Dürerplatz 1, 883828.', 'BSV-Mitteilungsblatt September 1942', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (494, 63, 1947, 'Bei der Deutschen Mannschaftsmeisterschaft in Sarstedt bei Hannover belegt die Schachsparte Berlin-Friedenau den 5.Platz von sechs Vereinen.', '', 'http://www.schachfreunde-buer.de/archiv/chronik/turniere/47_dmm.htm', 1);
