INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (511, 64, 192602, '<b>Wilmersdorf.</b> Die Simultanvorstellung des russischen Großmeisters Bogoljubow im Klubheim der S.V. Wilmersdorf, Lauenburgerstr.21, bedeutete für die Veranstalter einen großen Erfolg. Insgesamt wurden 37 Partien erledigt, wovon Bogoljubow 25 Partien gewann, 4 verlor und 8 remisierte. Gewonnen haben Ritter, Weinhagen, Langner und Richter, sämtlich S. V. Wilmersdorf, Steinbiß, Ziegert, Hörn, Schildberg, Steinsohn, Hirsch, Pestalozzi remisierten.  Von den Gästen konnte nur Pollack ein Remis erzielen.\n Bogoljubow, der sich die Sympathie seiner Gegner sowohl, wie der zahlreich anwesenden Zuschauer im vollsten Maße erworben hatte, hielt nach Schluß der Vorstellung eine kurze Ansprache, in der er den Wert des Schachspiels, besonders für die Jugend, betonte. Für seine Ausführungen erntete er reichen Beifall.', 'Deutsche Schachblätter Nr.4 15.Februar 1926, S.71', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (512, 64, 1933, '<b>Wilmersdorf</b> Lauenburger Str.21', 'Ranneforths Schachkalender 1934, S.55-57', '/archiv/chronik/1933/adressen/vereine.html', 1);
