INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (576, 68, 19510806, 'Am 6. August begann das Turnier um die Meisterschaft von Neukölln. Die vom Bezirksamt und von Neuköllner Firmen gestifteten wertvollen Preise entsprechen der Bedeutung dieses Turniers, das über den üblichen Vereinsrahmen hinausreicht und der Werbung für unser Schach dienen soll. Spiellokal: Boddinstraße 55/56 (Schule), Zeit: Montags, mittwochs, freitags ab 19¾ Uhr. Stand nach der 7. Runde: Dahl 5½, Esser 4 (1), Delander 3½, Schwarz 3 (2), Lehmann 3 (1), Köttler 3, Steneberg 2½, Ludwig 2½, Fröhlich 2 (2), Pinkall 2 (2), Markert 2 (1).', 'BSV-Mitteilungsblatt Nr.6 September 1951', '', 1);
