INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (579, 68, 19670907, 'Abstieg und Mitgliederschwund führen schließlich zur Fusion der ehemaligen Rivalen: Aus dem Schachclub Neukölln 03 und den Neuköllner Schachfreunden entsteht der Verein Schachfreunde Neukölln 03. Trotzdem erreichen die Schachfreunde den Aufstieg in die Berliner Stadtliga nicht mehr. Zwar floriert nun die Jugendarbeit; es gelingt jedoch nicht, die vielen Talente im Klub zu halten.', 'Chronik der Schachfreunde Neukölln', '', 1);
