INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (590, 70, 19511129, 'In der SG Karlshorst wurde eine Schach-Sektion gegründet. Spieltag jeden Mittwoch ab 19 Uhr im Restaurant Rudloff, Karlshorst, Müritzstraße. Sektionsleiter v.d.Linde, Karlshorst, Dorotheastr.8. Spielleiter Paul Bettin, Karlshorst, Treskowallee 81. Gäste herzlichst willkommen !', 'SCHACH 2/1952, S.32', '', 1);
