INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (619, 79, 1985, 'Man spricht unter Berlins Schachspielern von Antje Riedel, einer 17jährigen Schülerin der EOS Heinrich Schliemann: DDR-Meisterschaftsvierte als Debütantin; im Juli in Plowdiw erstmals die Norm einer FIDE-Meisterin erfüllt; DDR-Meisterin im Blitzschach. Ferner hat Antje am DDR-Meistertitel der männlichen (!) Jugendmannschaft ihrer BSG AdW Berlin wesentlichen Anteil. Schließlich leitet sie in ihrer Sektion mit Erfolg eine Trainingsgruppe von 8- bis 10jährigen Jungen und Mädchen.', 'Heinz Machatscheck in BZA 275 vom 23.11.1985', '', 1);
