INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (673, 112, 19510320, 'Damen-Meisterschaft 1951. Teilnehmerinnen: Redlich, Fischer, Daunke, Morlock, Frau Krause, Frl. Krause, Rudnick, Hannemann, Klabunde, Wilzek, Falk, Schulte. — Die 1. Runde begann am 20. 3. 51. Gespielt wird Dienstag und Freitag bei "Schachgesellschaft Eckbauer", Charlottenburg, Kaiserdamm 89.', 'BSV-Mitteilungsblatt Nr.1/1951 April', '', 1);
