INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (675, 112, 195104, 'Damenmeisterschaft 1951: Seit dem 20.3. wird Dienstags und Freitags von 19 Uhr ab bei der „Schachges. Eckbauer", Charlottenburg, Kaiserdamm 89, hartnäckig um den Berliner Damenmeistertitel gekämpft. Nach der 6. Runde führen die Damen Falk und Klabunde mit je 3 Punkten und 2 Hängepartien vor Frl. Krause mit 3 Punkten und 1 Hängepartie sowie Frau Krause mit 2 Punkten und 3 Hängepartien.', 'BSV-Mitteilungsblatt Nr.2/1951 Mai', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (676, 112, 19510415, 'Das Turnier um den Preis des Sportverbandes sowie um 5 wertvolle Preise, gestiftet von Gönnern des Vereins, brachte hochinteressante Kämpfe und wurde erst am Schlußtag entschieden. In einem Schlußwort dankte der Vorsitzende, Erich Stallknecht, allen Teilnehmern für die guten Leistungen und die sportliche Note, mit der die Kämpfe durchgeführt wurden.', 'BSV-Mitteilungsblatt Nr.2/1951 Mai', '/archiv/chronik/1951/sportausstellung/index.html', 1);
