INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (697, 113, 195104, 'Die Generalversammlung wählte einen neuen Vorstand. 1. Vorsitzender Max Pawlak, 2. Vorsitzender Josef Leibelt, Kassierer Curt Kühn, Spielleiter Rud. Seifarth. Spielabend: Donnerstag 19 Uhr, Sport-Kasino Neuendorffer Straße 18. (371962.)', 'BSV-Mitteilungsblatt Nr.2/1951 Mai', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (698, 113, 195106, 'Die ab 20.März bei der SG Eckbauer (Kaiserdamm 89, dienstags und freitags) stattfindende Berliner Damen-Meisterschaft gewinnt Berta Klabunde (Spandauer SV). Das ist der erste Titel nach 57 Jahren für den spielstarken Spandauer SV.

