INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (740, 116, 1951, 'Mannschaftsmeisterschaft 1951. Tabellen-Endstand der Zwischenliga: 1. Union-Oberschöneweide 14 (55), 2. Lasker 13 (54), 3. Wittenau 13 (53½), 4. Weißensee 11 (47), 5. Tempelhof 8 (45½), 6. Concordia 8 (45½), 7. Steglitz 7 (44), 8. Neukölln II 7 (42½), 9. Spandau II 5 (33), 10. Läufer Nord 4 (35).
