INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (744, 116, 19510726, 'Der Klub der Schachfreunde Berlin hat mit Unterstützung des Amtes für Jugendförderung Tempelhof einen halbjährigen Lehrgang "Jugend-Schulschach" im Schillerklub Tempelhof, am 26. Juli 1951 durch eine Simultanvorstellung an 32 Brettern für die besten Schüler abgeschlossen.
Schachfreund Faust mußte von 19 Partien 2 Siege den Lehrlingen überlassen. Schachfreund Gulweida gewann 12 Partien und eine wurde remis.
Es hatten sich für diesen Lehrgang über 70 Schüler gemeldet, verschiedene davon spielten schon recht beachtlich, also ein guter Zuwachs für das Berliner Schach.
Nach den großen Ferien, werden neue Lehrgänge unter Beteiligung aller Schulen des Bezirks Tempelhof durchgeführt.', 'BSV-Mitteilungsblatt Nr.5 August 1951', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (745, 116, 195110, 'Der "Klub der Schachfreunde Tempelhof" hat seinen Namen in "Schachklub Tempelhof" geändert. Es werden wieder regelmäßig Kurse für Jugendliche abgehalten. Im November beginnen die Lehrkurse für Anfänger in den Schulen. Der Verein hat anläßlich seines 20jährigen Bestehens ein Vereins-Jubiläums-Turnier in 8 Gruppen gestartet.', 'BSV-Mitteilungsblatt Nr.8 November 1951', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (746, 116, 195110, 'Spielleiter Stange hat den Klub auf Ingo-Wertung umgestellt.', 'BSV-Mitteilungsblatt Nr.8 November 1951', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (747, 116, 19511007, 'Am Sonntag, dem 7.10.51 fand der schon lange angesetzte Wettkampf Motor Treptow - S. K. Tempelhof statt. M. T. konnte leider nur 19 Bretter besetzen und verlor sehr hoch 5 - 14. Bei Tempelhof spielte am 1. Brett Nr. 14, am 2. Brett Nr. 18 und am 19. Brett Nr. 54 unserer Rangliste.', 'BSV-Mitteilungsblatt Nr.8 November 1951', '', 1);
