INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (749, 118, 1947, 'Die <i>Schachgruppe Prenzlauer Berg</i> wird von Eugen Natzmer geleitet.', '', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (750, 118, 1947, 'Die Jugendmannschaften von Oberschöneweide und Prenzlauer Berg maßen sich im Wettkampfe und trennten sich nach interessantem Spielverlauf mit 5:5 unentschieden. Der Rückkampf findet am 8.Juni, 9 Uhr, in Oberschöneweide, Wilhelminenhofstraße, statt (Zum Schloßpark, Winkler).', 'Schach-Express 5/1947, S.78', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (751, 118, 1947, 'Ein auf 50 Brettern angesetzter Freundschaftskampf der Bezirke Prenzlauer Berg und Wedding brachte für den diesjährigen Mannschaftsmeister schon vom 2.Brett ab Besetzungsschwierigkeiten. Wedding gewann mit 32½:13½ Punkten. Am 1.Brett siegte B.Koch (Prenzl.Berg) über K.Gumprich (Wedding).', 'Schach-Express 8/1947, S.126', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (752, 118, 19470608, 'Im Rückkampf der Jugendgruppe gegen Prenzlauer Berg siegte Oberschöneweide mit 6:4.', 'Schach-Express 8/1947, S.126', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (753, 118, 1948, 'Meister Sämisch gab in Berlin einige Blindspiel- und Simultanveranstaltungen. Beim Blindspiel in Friedenau an 8 Brettern war das Ergebnis +6 =2 und in Charlottenburg: +4 -1 =3. Seine Ergebnisse im Simultanspiel waren: Prenzlauer Berg +23 -7 =9, Wilmersdorf +19 -1 =4.', 'Caissa 15.März 1948, S.7', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (754, 118, 1948, 'Die Meisterschaft des Bezirks Prenzlauer Berg errang B.Koch 13½ vor Mai 12½, Lexandrowitsch 11, Hopp 10.', 'Deutsche Schachblätter Nr.9 Dezember 1948, S.143', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (755, 118, 1948, 'Im Mannschafts-K.o.-Turnier schlug der Berliner Meister Prenzlauer Berg in der Vorschlußrunde Charlottenburg mit 5:3 (B.Koch, P. - Dr.Lehmann, Ch. ½:½) und in der Schlußrunde Spandau mit 5½:2½ (Mroß, Sp. - B.Koch, P. 1:0). Die gut ausgeglichene Mannschaft von Prenzlauer Berg hat damit auch diesen Wettbewerb gewonnen.', 'Deutsche Schachblätter Nr.7 Oktober 1948, S.110', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (756, 118, 19480927, 'In einem Blitzturnier des Mannschaftsmeisters Prenzlauer Berg siegte B.Koch 11½ vor Bares, Lexandrowitsch 10½.', 'Deutsche Schachblätter Nr.8 November 1948, S.125', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (757, 118, 194901, 'Die Gruppe Prenzlauer Berg führte im Januar zwei Simultanspiele durch: Jugendmeister Bares +15, =2, -3 und Lexandrowitsch +16, =4, -5.', 'Deutsche Schachblätter Nr.3 März 1949, S.47', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (758, 118, 194902, 'Schlußstand der Mannschaftsmeisterschaft: Wedding 68½, Prenzlauer Berg 67½, Pankow 61, Tiergarten 59½, Spandau 57, Charlottenburg 54½, Wilmersdorf 53½, Steglitz, Friedrichshain 52½, Friedenau 50½, Neukölln 49, Prenzlauer Berg II 34 (mit Meister B.Koch an der Spitze) verlor also den Titel denkbar knapp an Wedding (mit Gumprich). Eigentlich entschied die zweite Mannschaft von Prenzlauer Berg den Wettkampf, indem sie gegen Wedding mit 0:10 verlor, gegen ihre "Brudermannschaft" aber hartnäckigen Widerstand leistete und nur  3½:6½ im Nachteil blieb.', 'Deutsche Schachblätter Nr.3 März 1949, S.47', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (759, 118, 194904, 'In Berlin siegte am Karfreitag eine Kombination Friedrichshain-Oberschöneweide gegen den Ostbezirk mit 39:20; die Gruppe Prenzlauer Berg eröffnete am gleichen Tage ihr neues Spiellokal in der Aschinger-Kantine mit einem Mannschaftsblitzturnier, das P.B. mit 60½ vor Turm I 57½, Turm II 40, Kreuzberg 36 gewann. Ein Einzelblitzturnier gewannen Blank, Pfeiffer 12½, Kubiczek 10, Natzmer 9. 
Die Schachgruppen des Berliner Westens spielten am Karfreitag im Sportheim Spandau ein Mannschaftsblitzturnier: Charlottenburg 33½, Spandau I 30, Spandau II 28, Siemensstadt 23, Steglitz 20. 
Im Winterturnier siegten in Mitte: Jehne, Kurt Schmidt je 14½; Neukölln-Nord: Siebenwirth 9 (aus 9); Wittenau: Vogel; Turm: Skibbe.
In den Sommermonaten trägt die Berliner Schachsparte wieder ein Mannschafts-k.o.-Turnier aus.', 'Deutsche Schachblätter Nr.6 Juni 1949, S.94', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (760, 118, 19490401, 'Die Schachgruppe schließt sich der BSG Eintracht 49 an. Eugen Natzmer bleibt Spartenleiter.', '', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (761, 118, 194906, 'Gruppenmeister der Schachgruppe Charlottenburg in Berlin wurde Dr.Lehmann 11½ knapp vor dem Vorjahresmeister Grohmann 11, Barnikol 10, Vogt 8½. Bei der Berliner Jugendmeisterschaft verteidigte P.Bares, Przl.Berg, seinen Titel erfolgreich, indem er im Stichkampf Darga, Wittenau, schlug.', 'Deutsche Schachblätter Nr.7 Juli 1949, S.109', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (762, 118, 19490805, 'Am 5.August besuchte Dr.Euwe die Schachgruppe Prenzlauer Berg zu einer Uhrenvorstellung an 8 Brettern. (Entgegen unseren Erwartungen hält Dr.Euwe eine Uhrenvorstellung gegen 8 selbst starke Gegner für leichter als ein Simultanspiel etwa gegen 30 schwächere Spieler.) Er spielte flott und sicher und erzielte das hervorragende Ergebnis von +7, =1, -0.', 'Deutsche Schachblätter Nr.9 September 1949, S.131/132', '/archiv/bsz/0031-0040/index.html#bsz0035', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (763, 118, 19490828, 'Der Sommerwettbewerb der Berliner Schachsparte, ein Mannschafts-K.o.-Turnier, begann mit 46 Mannschaften. In der Vorschlußrunde am 28.August siegten Eckbauer über Prenzlauer Berg und Wedding über Neukölln, je mit 6½:1½, so daß in der Schlußrunde am 4.9.49 Eckbauer und Wedding zusammentrafen. Mit 4½:3½ blieb Wedding knapp erfolgreich und holte sich damit nach der bereits gewonnenen Mannschaftsmeisterschaft auch diesen Wettbewerb (Pfeiffer 1 - Dr.Lehmann 0, Blank 1 - Gumprich 0, Schwabe ½ - Grohmann ½, 0 Hallbauer - Barnikol 1, 0 Wolf - Vogt 1, 1 Groneberg - Jurgschat 0, 0 Göcks - Nawothnig 1, 1 Wasenberg - Wildau 0).', 'Deutsche Schachblätter Nr.10 Oktober 1949, S.147', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (764, 118, 1950, 'B.Koch wurde zum viertenmal Bezirksmeister Prenzlauer Berg mit 9 vor Westphal 8½, Natzmer 7, Hopp 7, Strasdas 6½, Adeler 6½.', 'Deutsche Schachblätter Nr.7 Juli 1950, S.109', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (765, 118, 195001, '28.-29.01.1950: Eine Mannschaft der BSG Eintracht Berlin (mit B.Koch, Strasdas, Kubiczek, Westphal, Natzmer usw.) weilt in Magdeburg, mußte sich aber hinter Erfurt 17½ mit 16 Punkten mit dem zweiten Platz begnügen vor Magdeburg 14, Leipzig 12½.', 'Deutsche Schachblätter Nr.3 März 1950, S.45', '', 1);
