INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (800, 119, 1948, 'Das Sommerturnier der Schachgruppe Kreuzberg gewann Sternberg, der Gruppe Friedrichshain Mandel. In einem Simultanspiel Friedrichshain erzielte P.Mroß +17, =3.', 'Deutsche Schachblätter Nr.8 November 1948, S.125', '', 1);
