INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (810, 119, 1951, '<b>Schachclub Kreuzberg.</b> Klubmeister wurde Pawelczak nach Stichkampf vor Roesner. Sieger im Sommerturnier Gr. I Freitag mit 100%. Gr. II Thunig. Gr. III Strumpf. Blitzturniersieger wurde Kühn, in der Gr. II Doellert. Ein Freundschaftskampf gegen Neuköllner Schachfreunde an 25 Brettern endete 14½ : 10½ für Kreuzberg, das ohne I. Mannschaft spielte.', 'BSV-Mitteilungsblatt Nr.9 Dezember 1951', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (811, 119, 1951, '<b>Schachclub Kreuzberg.</b> Zur Einführung des neugegründeten Schachklubs "Weiße Dame" führte Kreuzberg 2 Freundschaftskämpfe durch. Der erste Kampf im Klubheim an 17 Brettern endete 8½:8½, der Rückkampf im Klubheim der "Weiße Dame" an 25 Brettern 19:6 für Kreuzberg. Im 1.Kampf spielten nur Spieler der 3.-5. Mannschaft, im Rückkampf war Kreuzberg durch Spieler der 2. Mannschaft verstärkt.', 'BSV-Mitteilungsblatt Nr.1 Januar 1952', '', 1);
