INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (815, 119, 19510413, 'Auf der Mitglieder-Hauptversammlung am 13. 4. 51 wurden gewählt zum 1. Vorsitzenden Bernh. E. Weiß, 2. Vorsitzenden Willi Trumpler, Schriftführer Georg Korb, Kassierer Arnold Menzel, Spielleiter Friedrich Simon und Hans Burmeister, Jugendleiter Hans Tribbensee, Schrankwart Gerhard Kiehl. Spielabende: Dienstag und Freitag ab 18 Uhr. Klublokal: Bezirksamtskantine,   Berlin SW 29,   Urbanstraße 20,   II. Aufgang  part. links.', 'BSV-Mitteilungsblatt Nr.3/1951 Juni', '', 1);
