INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (817, 119, 19510819, 'Am 19. August fand die erste Bezirksmeisterschaft im Bezirk Kreuzberg ihren Abschluß. Im Rahmen einer kleinen Feier überreichte Bürgermeister Kreßmann dem neuen Bezirksmeister Holze den von ihm gestifteten Ehrenpreis und Ehrenurkunden an die drei Besten des Turniers. Ergebnis: Sieger Holze 6½, II. Kühn 6, III. Heuer 4½, IV. Menzel 3½, V. Haum 2½, VI. Kaiser 2, VII. Kammerau 2, VIII. Kliehm 1 Punkt.', 'BSV-Mitteilungsblatt Nr.7 Oktober 1951', '', 1);
