INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (825, 120, 1951, 'Harald Lieb (Süd-West) qualifiziert sich für die Zwischenrunde der Berliner Jugend-Einzelmeisterschalt 1951/52.', 'BSV-Mitteilungsblatt Nr.1 Januar 1952', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (826, 120, 1951, '2 Mannschaften nehmen an der gemeinsamen BMM 1951/52 teil.', 'BSV-Mitteilungsblatt Nr.1 Januar 1952', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (827, 120, 19510201, 'Ein Jugendlicher namens Harald Lieb tritt in den Verein ein. Er wird später mehrfacher Berliner Meister.', '', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (828, 120, 195104, 'Mannschaftsmeisterschaft 1951 - B-Klasse Gr.1: 5.Platz Zehlendorf 2', 'BSV-Mitteilungsblatt Nr.1/1951 April', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (829, 120, 19510706, 'Am 6. Juli 1951 fand die Jahreshauptversammlung der Schachgesellschaft "Süd-West" Zehlendorf statt. Die Neuwahl des Vorstandes brachte folgendes Ergebnis: Vorsitzender: Dr. Erich Stüber, stellv. Vorsitzender: Heinz Riepenhausen, Kassierer: Heinz Fenners, Geräte- und Bücherwart: Frau Ingeborg Wilzeck, Jugendwart:  Gerhard Großkopf.
Am gleichen Abend fand die Preisverteilung für das Winterturnier statt. Klubmeister wurde Ulrich Knick vor Walter Mirschenz.', 'BSV-Mitteilungsblatt Nr.5/1951 August', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (830, 120, 195110, '<b>Zehlendorf (Schachges. Südwest):</b> Vereinsabend ist jetzt der Donnerstag. Das Amt des Spielleiters hat Schachfreund Gettner übernommen. Die Winterspielzeit wurde mit einem Blitzturnier eröffnet; unter 25 Teilnehmern siegten in der 1. Klasse Mirschenz vor dem Junior H. Lieb, in der 2. Klasse der Junior W. Lieb. Das Winterturnier mit 30 Teilnehmern wird gleichfalls in zwei Klassen gespielt. Im November sind besondere Spielabende für das Mannschaftstraining angesetzt. Im Dezember hält Dr. Heinz Lehmann einen Vortrag (anschließend Simultanspiel). Vereinslokal: Restaurant "Zum fröhlichen Wanderer", Zehlendorf, Fischerhüttenstr. 45 (Nähe Ubf. Krumme Lanke).', 'BSV-Mitteilungsblatt Nr.8 November 1951', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (831, 120, 195112, '<b>Zehlendorf (Schachgesellschaft Südwest).</b> Der für Dezember 1951 vorgesehene Vortragsabend von Dr. Heinz Lehmann mußte wegen Terminschwierigkeiten verlegt werden. Er findet am Donnerstag, dem 17. 1. 52, um 19.30 Uhr im Restaurant "Zum fröhlichen Wanderer", Fischerhüttenstr. 45, statt. Anschließend Simultanspiel. Gäste willkommen.', 'BSV-Mitteilungsblatt Nr.1 Januar 1952', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (832, 120, 1952, 'Auf der Jahreshauptversammlung der SG Südwest Zehlendorf wurde der bisherige Vorstand einstimmig wiedergewählt. Dr.Erich Stüber ist 1.Vorsitzender, Riepenhausen sein Stellvertreter.', '', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (833, 120, 1952, 'SG Südwest Zehlendorf verliert einen Blitzwettkampf an 14 Brettern gegen den SV Wilmersdorf mit 64:132.', '', '', 1);
