INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (837, 120, 198205, 'Bei der Vereinswahl treffen mit dem Vorsitzenden Anton Manz und Herausforderer Dr.Freerk Bulthaupt zwei Welten aufeinander. Bulthaupt, ein Anhänger des Profischachs, gewinnt und Anton Manz verläßt daraufhin mit rund 20 getreuen Amateuren den Verein und gründet den Schachklub Süd-West.', '', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (838, 120, 1988, 'Eine zweite Teilung gibt es nach den Wahlen. Michael Riedel, der chancenlos gegen Dr.Freerk Bulthaupt um den Vorsitz kandierte, gründete mit mit fast allen Kindern und einigen Jugendlichen und Erwachsenen die Zehlendorfer Königsjäger.', '', '', 1);
