INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (859, 121, 1981, 'Die anderen 4 Mannschaften hielten ihre Klassen ( 2. Bezirksklasse, 2. Stadtklasse und 4. Stadtklasse). Aufgrund des Weggangs einiger Spieler wurde die 5.te Mannschaft nach dieser Saison aufgelöst.', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
