INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (863, 121, 198203, 'Im März 1982 Sieg im Freundschaftsspiel gegen Armaturen Prenzlau mit 20:5. Sieger im Blitzturnier wird K. Welke vor D. Türtmann.', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (864, 121, 1983, 'K.-P. Wünsche wird Sieger im ET der BSG Armaturen Prenzlau. D. Türtmann wurde 6.ter.', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (865, 121, 1983, 'Die 1.te Mannschaft (Wünsche, Sucher, Ralf Seils, Türtmann, Welke, Körner, A. Kriese, Albert, Czikuß; Beste: Wünsche 6 aus 9 und Sucher 5,5 aus 9) steigt leider in die 1. Bezirksklasse ab. Die 2.te Mannschaft hielt die 2. Bezirksklasse. Die 3.te Mannschaft steigt in die 5. Stadtklasse ab. Die 4.te Mannschaft stieg wieder in die 5. Stadtklasse auf.', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (866, 121, 198309, 'Im September 1983 Sieg im Freundschaftsspiel gegen Armaturen Prenzlau mit 10,5:7,5 (Bester mit 2 Siegen: K. Welke).', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (867, 121, 198311, 'Im November 1983 wurde der Freundschaftskampf gegen Lech Poznan in Bernau mit 11,5:10,5 gewonnen (Bester mit 2 Siegen: K. Welke). Das Blitzturnier gewinnt Jörg Seils mit 13,5 Punkten aus 15 Partien, vor Walendowski (11) und K. Welke (10).', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (868, 121, 1984, 'Die 1. Mannschaft (Wünsche, Sucher, Czikuß, Welke, Türtmann, Ralf Seils, Albert, Körner, ; Beste: Wünsche 7 aus 9, Czikuß 6 aus 7, Welke 5 aus 6) stieg als Sieger wieder in die Bezirksliga auf. Die 2.te Mannschaft wurde "am grünen Tisch" (trotz 4 Mannschaftssiege!) gemeinsam mit Motor Wildau, aufgrund eines irrtümlich nicht durchgeführten Mannschaftsspiels, gestrichen. Die 3.te Mannschaft stieg wieder in die 4. Stadtklasse auf.', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (869, 121, 198402, 'Im Februar 1984 Sieg im Freundschaftsspiel gegen Armaturen Prenzlau mit 14:5 (Beste mit je 2 Siegen: Wünsche, Türtmann und Kuhfuß).', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (870, 121, 198409, 'Im September 1984 wurde das Rückspiel im Freundschaftskampf bei Lech Poznan (aufgrund einiger fehlender Stammspieler) klar mit 9:14 verloren (Beste: Körner und Schlief mit je 1,5 aus 2).', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (871, 121, 1985, 'Die 1.te Mannschaft (Wünsche, Czikuß, Welke, Türtmann, Sucher, Olaf Kriese, Ralf Seils, Körner, ; Bester: Wünsche 6,5 aus 8) wurde mit 43,5 Punkten Dritter in der Bezirksliga. Die 2.te Mannschaft (Bester Einzelspieler: Kurt Escher 5 aus 7) verfehlte das Ziel sofort wieder in die 2. Bezirksklasse aufzusteigen als Fünfter (36,5 Punkte) klar. Die 3.te Mannschaft wurde in der 4. Stadtklasse Sechster. Die 4.te Mannschaft hielt die 4. Stadtklasse als Siebter.', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (872, 121, 198505, 'K.-P. Wünsche wird im Mai 1985 Dritter beim ET der BSG Armaturen Prenzlau. D. Türtmann wurde 8.ter.', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (873, 121, 1986, 'Die 1.te Mannschaft (Wünsche, Czikuß, Türtmann, Welke, Ralf Seils, Sucher, Körner, Escher ; Bester: Wünsche 5 aus 7) wurde mit 33,5 Punkten Siebter in der Bezirksliga. Die 2.te Mannschaft (Beste Einzelspieler: Olaf Kriese 4 aus 4, Albert 4 aus 5) gelang es wieder in die 2. Bezirksklasse aufzusteigen (44,5 Punkte). Die 3.te Mannschaft hielt die 4. Stadtklasse mit 32 Punkten. Der 4.ten Mannschaft gelang überraschend der Aufstieg in die 4. Stadtklasse mit 40,5 Punkten (Beste: Eichhorn mit 5 aus 6 und Blaschke mit 4,5 aus 6 ). Aufgrund der guten Nachwuchsarbeit von Wilfried Schlief, gelang es wieder eine 5.te Mannschaft zu bilden. Diese schaffte auf Anhieb den Aufstieg in die 5.te Stadtklasse.', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (874, 121, 1986, 'Die 1. Jugendmannschaft von Lok Oberspree stieg in die Bezirksliga (Jugend) auf.', 'Internetseiten der Schachfreunde Nordost', 'http://taschenorakel.de/snob/archive.php/de/ie-default/history', 1);
