INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (930, 124, 1952, 'Ein kleines Einladungsturnier der SG Weißensee sah B.Koch ungeschlagen als Sieger mit 6 Punkten (aus 7). Zweiter wurde H.Platz (4½) vor Rhodin, B.Ullrich (je 4), Kirchhammer (3½), Seiffert, Störk (je 2½), Ahlberg (1).', 'SCHACH 10/1952, S.160', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (931, 124, 19520607, 'Anläßlich des Aufstiegs zur Stadtklasse veranstaltet der SC Weißensee, Sektion Schach, an diesem Sonnabend im Volkshaus Weißensee, Berliner Allee 210, ein Fest mit Wolf Gabbe und seinem Tanzorchester. Die gesamte Berliner Schachgemeinde wird auf diesem Weg herzlichst eingeladen.', 'SCHACH 9/1952, S.144', '', 1);
