INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (956, 130, 1951, '<b>Schachclub Kreuzberg.</b> Zur Einführung des neugegründeten Schachklubs "Weiße Dame" führte Kreuzberg 2 Freundschaftskämpfe durch. Der erste Kampf im Klubheim an 17 Brettern endete 8½:8½, der Rückkampf im Klubheim der "Weiße Dame" an 25 Brettern 19:6 für Kreuzberg. Im 1.Kampf spielten nur Spieler der 3.-5. Mannschaft, im Rückkampf war Kreuzberg durch Spieler der 2. Mannschaft verstärkt.', 'BSV-Mitteilungsblatt Nr.1 Januar 1952', '', 1);
