INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (988, 149, 193808, 'Zu Gruppenleitern wurden bestellt: Karl Hawemann in Hermsdorf, Friedrichstalerweg 18 (471328) für die Gruppe Hermsdorf und Jakob Hillbrunner in Berlin-Reinickendorf-West, Zobeltitzstr. 48, für die Gruppe Reinickendorf-West.', 'BSV-Mitteilungsblatt August/September 1938', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (989, 149, 193808, 'Die Einzelmeisterschaft des Bezirks Norden errang in diesem Jahre R. Lehmann-Humboldt nach Stichkampf gegen Brandes-Moabit. Der vorjährige Bezirksmeister Silbermann-Pankow war aus beruflichen Gründen nicht in der Lage, seinen Titel zu verteidigen. — Nachstehend die Turnierreihenfolge: 1. Lehmann-Humboldt 8+1½, 2. Brandes-Moabit 8+½, 3.-4. Sternberg und Bochow — beide Pankow 6½, 5. Groneberg-Humboldt 6, 6.—7. Seiffert-Humboldt und Striedl-Reinickendorf 5, 8.—9. Redlich-Moabit und Hoose-Humboldt 4½. 10. Kater-Moabit 2, 11. Gronki-Weißensee 0.', 'BSV-Mitteilungsblatt August/September 1938', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (990, 149, 1942, '<b>Reinickendorf-West:</b> Jakob Hillbrunner, Bln.-Reinickendorf-West, Zobeltitzstr. 48, Freitag, Restaurant Buchholz, Scharnweberstr. 13, 494813.', 'BSV-Mitteilungsblatt September 1942', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (991, 149, 19420918, '<b>Bezirk Norden.</b> In einer Simultanvorstellung am 18. September in der Gruppe Reinickendorf-West erzielte Teschner an 19 Brettern 16 Gewinne gegen 2 Verluste und ein Remis. Eine Sammlung für die Soldatenhilfe ergab dabei einen Betrag von 25 RM.', 'BSV-Mitteilungsblatt Oktober 1942', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (992, 149, 194911, 'Größere Schachveranstaltungen führte die Berliner Schachsparte am Bußtag durch. Im Norden spielten Tegel, Reinickendorf-Ost und -West sowie Reinickendorfer Füchse gegen Wedding/Humboldt 33:25. In Spandau blieb Eckbauer mit 22:16 erfolgreich. Im Süden siegte Kreuzberg 21½:12½ über Tempelhof-Mariendorf.', 'Deutsche Schachblätter Nr.12 Dezember 1949, S.190', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (993, 149, 19500218, 'Der Verein tritt dem wiedergegründetem Berliner Schachverband bei. Liste der Gründungsmitglieder:
<ul>
<li>Schachklub Kreuzberg e.V.</li>
<li>Schachspielgemeinschaft Reinickendorf-West</li>
