INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1188, 175, 19310923, 'Heute vollendete Meister Erhard Post, der hochverdiente 1. Vorsitzende der Berliner Schachgesellschaft, wie auch des Berliner und Brandenburgischen Schachverbandes, sein 50. Lebensjahr.<br>Meister Post gehört zu jenen seltenen Persönlichkeiten, die hohes schachliches Können mit ebenso grossen Organisationstalent verbinden. Und so bringen wir ihm zu seinem Ehrentage unsere herzlichen Grüsse und Wünsche dar, als dem ideal gesinnten und selbstloses Förderer unseres königlichen Spiels, dem tatkräftigen Führer des Berliner und Brandenburgischen Schachlebens und dem mehrfachen Inhaber des deutschen Vorkämpfertitels.', 'Deutsche Schachzeitung 1931, S.298 / Alan McGowan', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1189, 175, 19310926, 'Zu Ehren des Meisters Post fand heute im Klubheim der Berliner Schachgesellschaft eine grössere Feier statt.', 'Deutsche Schachzeitung 1931, S.299 / Alan McGowan', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1190, 175, 193112, 'Das Turnier um die Klubmeisterschaft der Berliner Schachgesellschaft ist ausserordentlich stark besetzt. Es beteiligen sich der holländische Meister Noteboom, die Berliner Meister Ahues, Babel, Helling, B. Koch, Pahl, Rellstab, K. Richter und Sämisch, sowie die jungen Talente Benkö und Nadel, die sich in den Vorkämpfen durch Siege in ihren Gruppen die Zulassung erstritten haben.', 'Deutsche Schachzeitung 1931, S.377 / Alan McGowan', '/archiv/chronik/1931/bsg/km.html', 1);
