INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1364, 181, 1926, '<b>Berliner Schachverband</b>

<p>(früher Freie Vereinigung Groß-Berliner Schachvereine). Umfaßt 26 Vereine Groß-Berlins: Berl. Schachges., Wilmersdorf, Springer, Verein von 1876, Sportklub Charlottenburg, Springer in Tegel, Schöneberg, Steglitz (Schallopp), Südwest, Spandau, Lichtenberg (Ges., Sport-Un.), Oberspree,  Anderssen, Steinitz, Karlshorst, Rex Albus, Werner Siemens, A.E.G., Friedenau, Neukölln, Damenbauer, Zehlendorf, Namenlos, Matt, Deutscher Sportklub, Klub 1920, Caissa, Weißensee, Eichwalde.</p>
<p>Vors.  E. Post;  Schriftf. P. Matzdorff; Schatzm. Dr. Junk, W. 15, Sächs. Str. 68.</p>
<p>Geschäftsstelle für Brandenburg und Berlin: Berliner Schachgesellschaft, Charlottenburg 2,  Kantstr. 8.</p>', 'Ranneforths Schachkalender 1926, S.65', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1365, 181, 19260223, 'Heute spielte Meister Bernhard Kagan im Berliner Schachklub "Springer", welcher in den Vereinskämpfen gesiegt hat, gleichzeitig 34 Partien, gewann 22, machte 11 unentschieden und verlor nur 1. Sämtliche Partien wurden in 4 Stunden beendet.', 'Deutsche Schachzeitung Nr.3 März 1926, S.68', '', 1);
INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1366, 181, 19500218, 'Der Verein tritt dem wiedergegründetem Berliner Schachverband bei. Liste der Gründungsmitglieder:
