INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1442, 201, 19380731, 'Am Sonntag, dem 31. Juli, veranstaltete der Bezirk Osten im "Orpheum" (früher Happolds), Berlin, Hasenheide, ein Schachpropagandaspiel. In zwei Klassen ging es "blitzmäßig" zu. Herr Röpstorff (Gruppe Baumschulenweg), Teschner (Schöneberg) und Post (Baumschulenweg) waren die drei Besten in der 1. Klasse. Eine Beratungspartie zwischen Neukölln und Oberschöneweide wurde von Neukölln gewonnen.', 'BSV-Mitteilungsblatt August/September 1938', '', 1);
