INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1486, 207, 193808, 'Der Bezirk Norden hat zwei schwere Verluste durch den Tod zu beklagen. Es verstarben der Leiter der Schachgruppe Hermsdorf Ernst Schmoldt und der Schatzmeister der Ortsgruppe Tegel Walter Quandt. Den beiden verdienten Schachkameraden wird das Berliner Schach ein ehrendes Andenken bewahren.', 'BSV-Mitteilungsblatt August/September 1938', '', 1);
