INSERT INTO `tl_vereinsregister_chronik` (id, pid, date, text, source, url, invisible) VALUES (1502, 210, 195004, '<b>Berliner Meisterschaften</b>\n Die Berliner Mannschaftsmeisterschaft wurde am Karfreitag in einer gemeinsamen Großveranstaltung im Tusculum, Berlin-Tempelhof, beendet. Freilich stand der Sieger in der Schachgesellschaft Eckbauer (Dr.Lehmann, Gumprich, Jurgschat, Grohmann, Steneberg, Vogt, Treumann, Kinzel, Barnikol, Probst) schon längere Zeit fest. Der Schlußstand lautet: Eckbauer 66, ZBG. Werner Seelenbinder 52, Turm 49, Pankow 48½, Wilmersdorf 48, Wedding (Vorjahrsmeister, durch Spielerabgänge geschwächt) 42, Freibauer 39½, Spandau 38, Steglitz 36, Oberschöneweide 31.', 'Deutsche Schachblätter Nr.5 Mai 1950, S.76', '', 1);
